package Sprint1.fast;

public class FastArraySBInt {
    // 19 999 storage
    // TODO: This can be static to avoid repeat
    // TODO: best case scenario we have a case and multiple sizes
    private StringBuilder memory = new StringBuilder("");
    private int length;

    public FastArraySBInt (int length) {
        this.length = length;
    }

    public int get (int index) {
        int realIndex = index * 2;

        // TODO: may be able to remove this later
        assert this.length > realIndex : "You are out of bounds";

        // System.out.println("The big side is " + Integer.toBinaryString((memory.charAt(realIndex) << 16)));
        // System.out.println("The small side is " + Integer.toBinaryString(memory.charAt(realIndex +1)));

        return (memory.charAt(realIndex) << 16) | memory.charAt(realIndex +1);
    }

    public void set (int index, int value) {
        int realIndex = index * 2;

        // TODO: may be able to remove this later
        assert this.length > realIndex : "You are out of bounds";

        // System.out.println("The big side is " + Integer.toBinaryString((char)(value >> 16)));
        // System.out.println("The small side is " + Integer.toBinaryString((char)(value & 0xFFFF)));

        memory.setCharAt(realIndex, (char)(value >> 16)); // Extracts the higher-order 16 bits
        memory.setCharAt(realIndex+1, (char)(value & 0xFFFF)); // Extracts the lower-order 16 bits
    }
}
