package Sprint1.fast;

public class FastArrayStringInt {
    // 19 999 storage
    // TODO: This can be static to avoid repeat
    // TODO: best case scenario we have a case and multiple sizes
    private String memory = "";
    private int length;
    public static int maxLength = 19999;

    public FastArrayStringInt (int length) {
        this.length = length;
    }

    public int get (int index) {
        int realIndex = index * 2;

        // TODO: may be able to remove this later
        assert this.length > realIndex : "You are out of bounds";

        // System.out.println("The big side is " + Integer.toBinaryString((memory.charAt(realIndex) << 16)));
        // System.out.println("The small side is " + Integer.toBinaryString(memory.charAt(realIndex +1)));

        return (memory.charAt(realIndex) << 16) | memory.charAt(realIndex +1);
    }

    public boolean getBit(int index) {
        // Calculate which character in the memory string has the bit
        int charIndex = index / 16;
    
        // Ensure the index is within bounds, it should be 19999 * 2^16
        assert 1310654464 > index : "You are out of bounds";
    
        // Calculate which bit in the character we are interested in
        int bitIndex = index % 16;
    
        // Extract the character
        char c = memory.charAt(charIndex);
    
        // Extract the specific bit (0 or 1)
        // Shift the bit to the least significant position and mask with 1
        return ((c >> bitIndex) & 1) == 1;
    }    

    public void set (int index, int value) {
        int realIndex = index * 2;

        // Ensure the index is within bounds
        assert this.length > realIndex : "You are out of bounds";

        // System.out.println("The big side is " + Integer.toBinaryString((char)(value >> 16)));
        // System.out.println("The small side is " + Integer.toBinaryString((char)(value & 0xFFFF)));

        memory = memory.substring(0, realIndex) + (char)(value >> 16) + (char)(value & 0xFFFF) + memory.substring(realIndex+1);
    }

    public void setBit(int index, boolean value) {
        // Calculate which character in the memory string has the bit
        int charIndex = index / 16;
    
        // Ensure the index is within bounds
        assert this.length > index : "You are out of bounds";
    
        // Calculate which bit in the character we are interested in
        int bitIndex = index % 16;
    
        // Extract the character
        char c = memory.charAt(charIndex);
    
        if (value) {
            // Set the bit to 1
            c |= (1 << bitIndex);
        } else {
            // Clear the bit to 0
            c &= ~(1 << bitIndex);
        }
    
        // Reconstruct the memory string with the modified character
        memory = memory.substring(0, charIndex) + c + memory.substring(charIndex + 1);
    }
    
}
